/**
 * If you are not familiar with React Navigation, refer to the "Fundamentals" guide:
 * https://reactnavigation.org/docs/getting-started
 *
 */
import { FontAwesome,Fontisto,MaterialIcons,Feather } from '@expo/vector-icons';
import { NavigationContainer, DefaultTheme, DarkTheme } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import * as React from 'react';
import { ColorSchemeName,View } from 'react-native';
import { createMaterialTopTabNavigator } from '@react-navigation/material-top-tabs';

import {Octicons,MaterialCommunityIcons} from '@expo/vector-icons';
import Colors from '../constants/Colors';
import useColorScheme from '../hooks/useColorScheme';
import ModalScreen from '../screens/ModalScreen';
import NotFoundScreen from '../screens/NotFoundScreen';
import ChatsScreen from '../screens/ChatsScreen';
import ChatRoomScreen from '../screens/ChatRoomScreen'
import ContactsScreen from '../screens/ContactsScreen';
import { RootStackParamList, MainTabParamList, RootTabScreenProps } from '../types';
import LinkingConfiguration from './LinkingConfiguration';
export default function Navigation({ colorScheme }: { colorScheme: ColorSchemeName }) {
  return (
    <NavigationContainer
      linking={LinkingConfiguration}
      theme={colorScheme === 'dark' ? DarkTheme : DefaultTheme}>
      <RootNavigator />
    </NavigationContainer>
  );
}

/**
 * A root stack navigator is often used for displaying modals on top of all other content.
 * https://reactnavigation.org/docs/modal
 */
const Stack = createNativeStackNavigator<RootStackParamList>();

function RootNavigator() {
  return (
    <Stack.Navigator
    screenOptions={{
      headerShadowVisible:false,
      headerStyle: {
        backgroundColor: Colors.light.tint,

      },
      headerTintColor: Colors.light.background,

      headerTitleStyle: {
        fontWeight: 'bold',

      }

    }}
    >
      <Stack.Screen name="Root" component={MainTabNavigator} options={{
        title:"MyChat",
        headerRight: () => (
          <View style = {{flexDirection: 'row', width: 60,justifyContent: 'space-between', marginRight:10}}>
          <Octicons name = "search" size = {22} color = {'white'}/>
          <MaterialCommunityIcons name = 'dots-vertical' size = {22} color = {'white'}/>
          </View>
        )

        }} />
        <Stack.Screen
        name="Contacts"
        component={ContactsScreen}
      />
      <Stack.Screen name="ChatRoom" component={ChatRoomScreen} options={({route}) =>  ({
        title:route.params.name,
        headerRight: () =>(
          <View style = {{flexDirection:'row', width: 100,justifyContent: 'space-between', marginRight:10}}>
            <MaterialIcons name="call" size={22} color={'white'}/>
            <Feather name ="video" size={22} color={'white'}/>
            <MaterialCommunityIcons name="dots-vertical" size={22} color={'white'}/>
          </View>
        )

      })}


         />
      <Stack.Screen name="Conatacts" component={NotFoundScreen} options={{ title: 'Oops!' }} />
      <Stack.Screen name="NotFound" component={NotFoundScreen} options={{ title: 'Oops!' }} />

      <Stack.Group screenOptions={{ presentation: 'modal' }}>
        <Stack.Screen name="Modal" component={ModalScreen} />
      </Stack.Group>
    </Stack.Navigator>
  );
}

/**
 * A bottom tab navigator displays tab buttons on the bottom of the display to switch screens.
 * https://reactnavigation.org/docs/bottom-tab-navigator
 */
const MainTab = createMaterialTopTabNavigator<MainTabParamList>();

function MainTabNavigator() {
  const colorScheme = useColorScheme();

  return (
    <MainTab.Navigator
      initialRouteName="Chats"
      screenOptions={{
        tabBarShowIcon:true,
        tabBarActiveTintColor:Colors[colorScheme].background,

        tabBarStyle:{
          backgroundColor: Colors[colorScheme].tint,
        },

        tabBarIndicatorStyle:{
          backgroundColor:Colors[colorScheme].background,
          height:4,
        },
        tabBarLabelStyle:{
          fontWeight:'bold',
        }
      }}>
      <MainTab.Screen
        name="Camera"
        component={ContactsScreen}
        options={{

          tabBarIcon:({color})=><Fontisto name="camera" color={color} />,
          tabBarLabel:()=>null,

        }}
      />
      <MainTab.Screen
        name="Chats"
        component={ChatsScreen} options={{tabBarLabelStyle:{fontWeight:"bold",fontSize:12}}}

      />
      <MainTab.Screen
        name="Contacts"
        component={ContactsScreen} options={{tabBarLabelStyle:{fontWeight:'bold',fontSize:12}}}

      />
      <MainTab.Screen
        name="Status"
        component={ContactsScreen}options={{tabBarLabelStyle:{fontWeight:'bold',fontSize:12}}}

      />
    </MainTab.Navigator>
  );
}

/**
 * You can explore the built-in icon families and icons on the web at https://icons.expo.fyi/
 */
function TabBarIcon(props: {
  name: React.ComponentProps<typeof FontAwesome>['name'];
  color: string;
}) {
  return <FontAwesome size={30} style={{ marginBottom: -3 }} {...props} />;
}
