import React, {useState,useEffect} from 'react';
import {View, Text, TextInput, KeyboardAvoidingView,TouchableOpacity,Platform,VariantsBox} from "react-native";
import SearchableDropdown from 'react-native-searchable-dropdown';
import styles from './styles';
import{API,Auth,graphqlOperation,} from "aws-amplify";
import {createEvents,updateChatRoom} from "../../src/graphql/mutations";
import {listUsers} from '../../src/graphql/queries';
import {
  MaterialCommunityIcons,
  MaterialIcons,
  FontAwesome5,
  Entypo,
  Fontisto,
} from '@expo/vector-icons';

const EventInput = () => {

  //const {chatRoomID} = props;
  const [description, setDescription] = useState('');
  const [myUserId, setMyUserId] = useState('');
  const [nextSelectedInvite,setNextSelectedInvite] = useState('');
  const [inviteList,setInviteList] = useState([]);
  const [filterFriendList, setFilterFriendList] = useState([]);
  const [users,setUsers] = useState([]);
  useEffect(() => {
      const fetchUsers = async () => {
          try{
              const userData = await API.graphql(graphqlOperation(listUsers));
              console.log(userData);
              setUsers(userData.data.listUsers.items);
          }catch(e){
              console.log(e);
          }
      }
     fetchUsers();
  }, [])
    useEffect(() => {
      const fetchUser = async () => {
        const userInfo = await Auth.currentAuthenticatedUser();

        setMyUserId(userInfo.attributes.sub);
      }
      fetchUser();
    },[])
  const onMicrophonePress = () => {
    console.warn('Microphone')
  }
  const onSendPress = async () => {

    // send the message to the backend
    try{
     const newEventData =  await API.graphql(graphqlOperation(createEvents,{input:{content:description,userID:myUserId,chatRoomID}}))
     console.log(newEventData);

    }catch(e){
      console.log(e);
    }


  }

  const onPress = () => {
        onSendPress();
  }
  const filterFriend = (friend) => {
      console.log("typing invites name")

      let list = [];
      users.forEach((element) => {
          if(element.name.includes(friend))
             list.push(element);
        });
     setFilterFriendList(list);
  }



  return (
    <KeyboardAvoidingView behavior={Platform.OS == "ios"?"padding":"height"}
    keyboardVerticalOffset ={100}style={{width:'100%'}}>
    <View style={styles.container}>
      <View style={styles.mainContainer}>

        <TextInput
          placeholder={"Type a message"}
          style={styles.textInput}
          multiline
          value={description}
          onChangeText={setDescription}
        />

      </View>
      <View style={styles.mainContainer}>


      <SearchableDropdown
            multi={true}
            selectedItems={inviteList}
            onItemSelect={(item) => {
              const items =inviteList;
              items.push(item)
              setInviteList([...items]);
            }}
            containerStyle={{ padding: 5 }}
            onRemoveItem={(item, index) => {
              const items = filterFriendList.filter((sitem) => sitem.id !== item.id);
              setInviteList([...items]);
            }}
            itemStyle={{
              padding: 10,
              marginTop: 2,
              backgroundColor: '#ddd',
              borderColor: '#bbb',
              borderWidth: 1,
              borderRadius: 5,
            }}
            itemTextStyle={{ color: '#222' }}
            itemsContainerStyle={{ maxHeight: 140 }}
            items={filterFriendList}
            defaultIndex={2}
            chip={true}
            resetValue={false}
            textInputProps={
              {
                placeholder: "placeholder",
                underlineColorAndroid: "transparent",
                style: {
                    padding: 12,
                    borderWidth: 1,
                    borderColor: '#ccc',
                    borderRadius: 5,
                },
                onTextChange: text => filterFriend(text)
              }
            }
            listProps={
              {
                nestedScrollEnabled: true,
              }
            }
          />
   </View>


    </View>
    </KeyboardAvoidingView>
  )
}

export default EventInput;
