import { StyleSheet } from 'react-native';

const styles = StyleSheet.create({
  container: {
    flexDirection: 'row',
    flex:1,
    width: "100%",
    justifyContent: 'space-between',

  },

  lefContainer: {
    flex:1,
    flexDirection: 'column',
    padding:4,
    marginRight:4,
    justifyContent: 'space-around',
    fontWeight:"bold",
    borderColor:"white",
    borderRadius:5,
    backgroundColor:'rgba(52, 52, 52, 0.8)'

  },
  midContainer: {
    flexDirection: 'column',
    flex:3,
    padding:5,
    borderWidth:2,
    borderColor:"white",
    borderRadius:5,
    margin:2,
    width:'90%',
  },
  avatar: {
    width: 60,
    height: 60,
    borderRadius: 50,
    marginRight: 15,
  },
  username: {
    fontWeight: 'bold',
    fontSize: 16,

    color: 'white',
  },
  status: {
    color:"white",
    fontSize: 20,
    fontFamily: 'Bodoni 72',
  },
});

export default styles;
