import React from 'react';
import {
  View,
  Text,
  Image,
  TouchableWithoutFeedback
} from "react-native";
import { ChatRoom } from "../../types";
import styles from "./style";
import moment from "moment";
import { useNavigation } from '@react-navigation/native';
import {
  API,
  graphqlOperation,
  Auth,

}from 'aws-amplify';
import {useEffect,useState} from 'react';
import {onCreateMessage} from '../../src/graphql/subscriptions';
import { Entypo } from '@expo/vector-icons';
export type ChatListItemProps = {
  chatRoom: ChatRoom;

}

const ChatListItem = (props: ChatListItemProps) => {
  const { chatRoom } = props;

  const [otherUser,setOtherUser] = useState(null);
  const[newMessage,setNewMessage] = useState(false);

  const navigation = useNavigation();

  const user = chatRoom.chatRoomUsers.items[1].user;

  useEffect (() =>{
    const getOtherUser = async () => {
      const userInfo = await Auth.currentAuthenticatedUser();
      if(chatRoom.chatRoomUsers.items[0].user.id === userInfo.attributes.sub){
        setOtherUser(chatRoom.chatRoomUsers.items[1].user);
      }else{
        setOtherUser(chatRoom.chatRoomUsers.items[0].user);
      }

    }

    getOtherUser();
  },[])

  console.log(chatRoom);

  useEffect(() =>{
    const subscription = API.graphql(
      graphqlOperation(onCreateMessage)).subscribe({next:(data) => {
        const newMessage = data.value.data.onCreateMessage;
        console.log(newMessage);


         console.log(data)

         setNewMessage(current => !current);



      }})
      return () => subscription.unsubscribe();
    },[])

  const onClick = () => {
    navigation.navigate("ChatRoom", {
      id: chatRoom.id,
      name: user.name,
    })
  }
  if(!otherUser){
    return null;
  }

  return (
    <TouchableWithoutFeedback onPress={onClick}>
      <View style={styles.container}>
        <View style={styles.lefContainer}>

          <Image source={{ uri: otherUser.imageUri }} style={styles.avatar}/>

          <View style={styles.midContainer} >
            <Text style={styles.username}>{otherUser.name}</Text>
            <Text numberOfLines={2} style={styles.lastMessage}>{chatRoom.lastMessage ? `${chatRoom.lastMessage.user.name}: ${chatRoom.lastMessage.content}` : "none"}</Text>
          </View>

        </View>

        <Text style={styles.time}>
          {chatRoom.lastMessage && moment(chatRoom.lastMessage.createdAt).format("DD/MM/YYYY")}
        </Text>
      </View>
    </TouchableWithoutFeedback>
  )
};

export default ChatListItem;
