import {StyleSheet} from 'react-native';

const styles = StyleSheet.create({
    container:{
        flexDirection:'row',
        width:'100%',
        justifyContent:'space-between',
        padding:10,
    },
    leftContainer: {
    flexDirection: 'row',
    },
    avatar:{
        width:60,
        height:60,
        marginRight:10,
        borderRadius:50,

    },
    userName:{
        fontWeight:'400',
        fontSize: 18,
    },
    lastMessage:{
        fontSize:16,
        color:'grey',
        paddingTop:0,
    }
});

export default styles;
