import { StyleSheet } from "react-native";
import Colors from "../../constants/Colors";

const styles = StyleSheet.create({
  container: {

    borderColor:"white",
    margin: 10,
    alignItems: 'center',
    justifyContent:'center',
    borderRadius:5,
    backgroundColor:'rgba(52, 52, 52, 0.8)',
    flexDirection: 'column',
    width:'100%'

  },
  mainContainer: {

    height:40,
    width:'90%',
    backgroundColor: 'white',
    padding: 1,
    borderRadius: 25,
    margin:5,
    marginBottom:0

  },
  textInput: {
   padding:5
  },
  icon: {
    marginHorizontal: 5,
  },
  nameCardContainer:{

    alignItems: 'center',
    color:'black',
    borderLeftWidth:2,
    borderTopWidth:.5,
    borderColor:'grey',
    borderRadius:8,
    height:30,
    width:500,
    backgroundColor:'white'

  },
  nameCardText:{
    color:'black',

    fontWeight: '700',
  },
  buttonContainer: {
    backgroundColor: Colors.light.tint,
    borderRadius: 25,
    width: 50,
    height: 50,
    justifyContent: 'center',
    alignItems: 'center',
  }
})

export default styles;
