import { MaterialCommunityIcons } from '@expo/vector-icons';
import React,{useState} from 'react';
import {View, TextInput} from 'react-native';
import styles from './styles';
import { FontAwesome5,Entypo,MaterialIcons,Fontisto } from '@expo/vector-icons';

const InputBox = () =>{
    const [message,setMessage] = useState('');
    return (
        <View style={styles.container}>
        <View style={styles.mainContainer}>
        <FontAwesome5 name = "laugh-beam" size={24} color='grey'></FontAwesome5>

        <TextInput placeholder = {"Type a message"}style = {styles.textInput}multiline value = {message} onChangeText={ setMessage}/>

        <Entypo name = 'attachment' size={24} color="grey" style = {styles.icon}></Entypo>
        <Fontisto name = 'camera' size={24} color = 'grey' style = {styles.icon}></Fontisto>
        </View>


        <View style = {styles.buttonContainer}>
        {!message
        ? <MaterialIcons name='mic' size={24} color='white'></MaterialIcons>:
          <MaterialIcons name='send' size={28} color='white'/>

    }
        </View>
        </View>
    )
}
export default InputBox;
