export default function EventsScreen() {
    const route = useRoute();
    const [event,setEvent] = useState([]);
    useEffect(() => {
        console.log(event);
        fetchChatRooms();
    },[event]);
    const fetchChatRooms = async () => {
      try{

        //pull data-->loop thru eventRooms comparing new event invites--> 
        //if no group exists then create new group along with n eventUsers, else create event with 
        //said group.
          const userInfo = await Auth.currentAuthenticatedUser();

          const userData = await API.graphql(
              graphqlOperation(
                  getUser, {
                      id: userInfo.attributes.sub,
                  }
              )
          )
          //JSON.stringify(playersOne) == JSON.stringify(playersTwo)
          const rooms = userData.data.getUser.eventUser.items;
          let listevents = [];
          let isDuplicate = false;
          //if(JSON.stringify(event.eventRoom.eventUsers.items)==JSON.stringify(event.invites)){isDuplicate = true;}

          rooms.map((event) => {
            let users = [];
            event.eventRoom.eventUsers.items.map((item) => users.push(item.user))
            console.log(JSON.stringify(users));
            console.log(JSON.stringify(event.invites));
            if(JSON.stringify(users)==JSON.stringify(event.invites)){isDuplicate = true;}
          })

         // setEventRooms(listevents);

          console.log(userData.data);
          console.log(isDuplicate);
      }catch(e){
          console.log(e);
      }

 }


  return (
    <View style={styles.container}>

     <EventInput postEvent = {setEvent}/>

    </View>
  );
}

