import React, {useState,useEffect,Fragment} from 'react';
import {View,FlatList, Text, TextInput, TouchableWithoutFeedback,KeyboardAvoidingView,Platform,StyleSheet} from "react-native";
import styles from './styles';
import{API,Auth,graphqlOperation,} from "aws-amplify";
import {createEvents,updateChatRoom} from "../../src/graphql/mutations";
import {listUsers} from '../../src/graphql/queries';
import {
  MaterialCommunityIcons,
  MaterialIcons,
  FontAwesome5,
  Entypo,
  Fontisto,
} from '@expo/vector-icons';

const EventInput = () => {

  //const {chatRoomID} = props;
  const [description, setDescription] = useState('');
  const [myUserId, setMyUserId] = useState('');
  const [nextSelectedInvite,setNextSelectedInvite] = useState('');
  const [inviteList,setInviteList] = useState([]);
  const [filterFriendList, setFilterFriendList] = useState([]);
  const [users,setUsers] = useState([]);

  useEffect(() => {
      const fetchUsers = async () => {
          try{
              const userData = await API.graphql(graphqlOperation(listUsers));
              console.log(userData);
              setUsers(userData.data.listUsers.items);
          }catch(e){
              console.log(e);
          }
      }
     fetchUsers();
  }, [])
  useEffect(() =>{
    setFilterFriendList([]);
    if(nextSelectedInvite == ""){ return;}

        let list = [];
        users.forEach((element) => {
            if(element.name.toLowerCase().includes(nextSelectedInvite.toLowerCase()))
               list.push(element);
          });
       setFilterFriendList(list);

       console.log(filterFriendList)

  },[nextSelectedInvite])
    useEffect(() => {
      const fetchUser = async () => {
        const userInfo = await Auth.currentAuthenticatedUser();

        setMyUserId(userInfo.attributes.sub);
      }
      fetchUser();
    },[])
  const onMicrophonePress = () => {
    console.warn('Microphone')
  }
  const onSendPress = async () => {

    // send the message to the backend
    try{
     const newEventData =  await API.graphql(graphqlOperation(createEvents,{input:{content:description,userID:myUserId,chatRoomID}}))
     console.log(newEventData);

    }catch(e){
      console.log(e);
    }


  }

  const onClick = (name) => {
        // onSendPress();
        console.log("click")
  }


  const NameCard = ({ item }) => (
    <TouchableWithoutFeedback onPress={onClick}>
    <View style={styles.nameCardContainer}>

      <Text style={styles.nameCardText} >{item.name}</Text>
    </View>
    </TouchableWithoutFeedback>
  );

  return (
    <KeyboardAvoidingView behavior={Platform.OS == "ios"?"padding":"height"}
    keyboardVerticalOffset ={100}style={{width:'100%'}}>
    <View style={styles.container}>
      <View style={styles.mainContainer}>

        <TextInput
          placeholder={"Type a message"}
          style={styles.textInput}
          multiline
          value={nextSelectedInvite}
          onChangeText={setNextSelectedInvite}
        />  
      </View>
      <View style={styles.mainContainer}>

     <TextInput
       placeholder={"Type a message"}
       style={styles.textInput}
       multiline
       value={nextSelectedInvite}
       onChangeText={setNextSelectedInvite}
     /> 
      <FlatList
        data={filterFriendList}
        renderItem={({item}) => <NameCard item ={item} ></NameCard>}
        keyExtractor={item => item.id}
      /> 
   </View>



    </View>





    </KeyboardAvoidingView>
  )

}

export default EventInput;
