import { StyleSheet } from "react-native";
import Colors from "../../constants/Colors";

const styles = StyleSheet.create({
  container: {

    borderColor:"white",
    margin: 10,
    alignItems: 'center',
    justifyContent:'center',
    borderRadius:5,
    backgroundColor:'rgba(52, 52, 52, 0.8)',
    flexDirection: 'column',
    width:'100%'

  },
  mainContainer: {


    width:'90%',
    backgroundColor: 'white',
    padding: 3,
    borderRadius: 25,
    margin:5,
    marginBottom:0

  },
  textInput: {
   padding:5
  },
  icon: {
    marginHorizontal: 5,
  },
  searchContainer:{
    width:'90%',
    backgroundColor:'white',
    borderRadius:2
  },
  nameCardContainer:{
    flexDirection:'row',
    alignItems: 'center',
    color:'black',
    width:'100%',
    borderTopWidth:.5,
    borderColor:'grey',
    borderRadius:2,
    height:30,

    backgroundColor:'white'

  },
  avatar: {
    width: 40,
    height: 40,
    borderRadius: 50,
    marginRight: 15,
    backgroundColor:'black'
  },
  nameCardText:{
    color:'grey',
    paddingLeft:12,
    paddingRight:20,
    fontWeight: '700',
  },
  buttonContainer: {
    backgroundColor: Colors.light.tint,
    borderRadius: 25,
    width: 50,
    height: 50,
    justifyContent: 'center',
    alignItems: 'center',
  }
})

export default styles;
