import { StyleSheet } from "react-native";
import Colors from "../../constants/Colors";

const styles = StyleSheet.create({
  container: {

    borderColor:"white",
    margin: 10,
    alignItems: 'center',
    justifyContent:'center',
    borderRadius:5,
    backgroundColor:'rgba(52, 52, 52, 0.8)'
  },
  mainContainer: {
    flexDirection: 'row',
    backgroundColor: 'white',
    padding:10,
    shadowRadius:10,
    shadowColor:'white',
    borderRadius: 25,
    margin: 10,
    flex: 1,
    alignItems: 'flex-end',

  },
  textInput: {
    flex: 1,
    marginHorizontal: 10,
    padding:0,
    marginTop:10,
    borderStyle:undefined
  },
  icon: {
    marginHorizontal: 5,
  },
  nameCardContainer:{
    flex: 1,
    alignItems: 'center',
    color:'white',
    borderLeftWidth:2,
    borderTopWidth:.5,
    borderColor:'white',
    borderRadius:8,
    height:'10vh',
    width:'60vw',
    backgroundColor:'black'

  },
  nameCardText:{
    color:'white',
    fontSize:'1rem',
    fontWeight: 'bold',
  },
  buttonContainer: {
    backgroundColor: Colors.light.tint,
    borderRadius: 25,
    width: 50,
    height: 50,
    justifyContent: 'center',
    alignItems: 'center',
  }
})

export default styles;
