import { StyleSheet } from "react-native";
import Colors from "../../constants/Colors";

const styles = StyleSheet.create({
  container: {

    borderColor:"white",
    margin: 10,
    alignItems: 'center',
    justifyContent:'center',
    borderRadius:5,
    backgroundColor:'rgba(52, 52, 52, 0.8)'
  },
  mainContainer: {
    flexDirection: 'row',
    backgroundColor: 'white',
    padding: 10,
    borderRadius: 25,
    margin: 10,
    flex: 1,
    alignItems: 'flex-end',
  },
  textInput: {
    flex: 1,
    marginHorizontal: 10
  },
  icon: {
    marginHorizontal: 5,
  },
  nameCardContainer:{
    flex: 1,
    alignItems: 'center',
    color:'white',
    borderColor:'white'
  },

  buttonContainer: {
    backgroundColor: Colors.light.tint,
    borderRadius: 25,
    width: 50,
    height: 50,
    justifyContent: 'center',
    alignItems: 'center',
  }
})

export default styles;
