import {StyleSheet} from 'react-native';
import Colors  from '../../constants/Colors';
const styles = StyleSheet.create({
    container:{
        flexDirection:'row',
        margin:10,
    },
    mainContainer:{
        flexDirection:"row",
        backgroundColor:'white',
        padding: 10,

        borderRadius:50
    },
    buttonContainer:
    {
        backgroundColor: Colors.light.tint,
        borderRadius: 50,
        width: 50,
        height:50,
        justifyContent:'center'

    }

})

export default styles;
